package main;

public class EjemploVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Numericas
		String numericas = "Numericas";
		System.out.println(numericas);
		int numero = 2;
		System.out.println(numero);
		numero = 10;
		System.out.println(numero);
		
			//Float
			String NumeroConComa = "NumeroConComa";
			System.out.println(NumeroConComa);
			float NumeroConComas = 2.7f;
			System.out.println(NumeroConComas);
			NumeroConComas = 10.2f;
			System.out.println(NumeroConComas);
		
				//Double
				String NumeroConComaMasLargo = "Double";
				System.out.println(NumeroConComaMasLargo);
				double double1 = 10.5;
				System.out.println(double1);
				double1 = 20.2;
				System.out.println(double1);
		
					//Char
					String caracter = "Caracter";
					System.out.println(caracter);
					char caracter1 = 'G';
					System.out.println(caracter1);
					caracter1 = 'P';
					System.out.println(caracter1);
		
						//String
						String cadena = "Cadena";
						System.out.println(cadena);
						String nombre = "Gorka";
						System.out.println(nombre);
						nombre = "Mikel";
						System.out.println(nombre);
		
							//Boolean
							String booleano = "Booleano";
							System.out.println(booleano);
							boolean booleano1 = true;
							System.out.println(booleano1);
							booleano1 = false;
							System.out.println(booleano1);
		

	}

}
